<?php    $palabra = $_REQUEST['q']; 	    echo obtenerPersonal($palabra);    flush();   	    function obtenerPersonal($palabra) {   			include_once('../modelo/diseno.php');				$ds = new diseno();		        $conexionBD = mysqli_connect('localhost','root','sql','paliativos');		//$conexionBD = mysqli_connect('mysql3000.mochahost.com','admproy_panama','cppanama2014','admproy_cuidados_paliativos_panama');        	   if (!$conexionBD) {            die('No se pudo conectar: ' . mysqli_error($conexionBD));        }				$consultaSQL = 'SELECT NO_CEDULA, concat(NO_CEDULA," ",PRIMER_NOMBRE," ",APELLIDO_PATERNO) AS NOMBRE FROM datos_profesionales_salud WHERE concat(NO_CEDULA," ",PRIMER_NOMBRE," ",APELLIDO_PATERNO) LIKE "%'.$palabra.'%" ORDER BY NO_CEDULA LIMIT 100;'; 			        $datos = mysqli_query($conexionBD, $consultaSQL);		        $coincidencias = ''; // Variable con resultados        while($valores = mysqli_fetch_array($datos)) { // Recorre los datos              $coincidencias .= $valores['NOMBRE'].'|'.$valores['NO_CEDULA']."\n";        }        mysqli_close($conexionBD); // Cierra la conexin                return UTF8_encode($coincidencias); // Devuelve la lista    }?>