<?php    $palabra = $_REQUEST['buscar'];     echo obtenerPersonal($palabra);    flush();       function obtenerPersonal($palabra) {   		include_once('../modelo/Diseno.php');		$ds = new diseno();        $conexionBD = mysqli_connect('localhost','root','sql','cuidados_paliativos_panama');        if (!$conexionBD) {            die('No se pudo conectar: ' . mysqli_error($conexionBD));        }	    $consultaSQL = 		'SELECT NO_CEDULA, concat(NO_CEDULA," ",PRIMER_NOMBRE," ",SEGUNDO_NOMBRE," ",APELLIDO_PATERNO," ",APELLIDO_MATERNO) as NOMBRE		FROM datos_profesionales_salud WHERE concat(NO_CEDULA," ",PRIMER_NOMBRE," ",SEGUNDO_NOMBRE," ",APELLIDO_PATERNO," ",APELLIDO_MATERNO) LIKE "%'.$palabra.'%" ORDER BY NOMBRE'; 	        $datos = mysqli_query($conexionBD, $consultaSQL);        $coincidencias = ''; // Variable con resultados        while($valores = mysqli_fetch_array($datos)) { // Recorre los datos              $coincidencias .= $valores['NOMBRE'].'|'.$valores['NO_CEDULA']."\n";        }        mysqli_close($conexionBD); // Cierra la conexin        return $ds->latino($coincidencias); // Devuelve la lista    }?>